"use client";

import useEmblaCarousel, {
  type UseEmblaCarouselType,
} from "embla-carousel-react";
import React from "react";
import { Button } from "@/components/ui/8bit/button";
import { cn } from "@/lib/utils";

type CarouselApi = UseEmblaCarouselType[1];
type UseCarouselParameters = Parameters<typeof useEmblaCarousel>;
type CarouselOptions = UseCarouselParameters[0];
type CarouselPlugin = UseCarouselParameters[1];

type CarouselProps = {
  opts?: CarouselOptions;
  plugins?: CarouselPlugin;
  orientation?: "horizontal" | "vertical";
  setApi?: (api: CarouselApi) => void;
};

type CarouselContextProps = {
  carouselRef: ReturnType<typeof useEmblaCarousel>[0];
  api: ReturnType<typeof useEmblaCarousel>[1];
  scrollPrev: () => void;
  scrollNext: () => void;
  canScrollPrev: boolean;
  canScrollNext: boolean;
} & CarouselProps;

const CarouselContext = React.createContext<CarouselContextProps | null>(null);

function useCarousel() {
  const context = React.useContext(CarouselContext);

  if (!context) {
    throw new Error("useCarousel must be used within a <Carousel />");
  }

  return context;
}

const Carousel = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement> & CarouselProps
>(
  (
    {
      orientation = "horizontal",
      opts,
      setApi,
      plugins,
      className,
      children,
      ...props
    },
    ref
  ) => {
    const [carouselRef, api] = useEmblaCarousel(
      {
        ...opts,
        axis: orientation === "horizontal" ? "x" : "y",
      },
      plugins
    );
    const [canScrollPrev, setCanScrollPrev] = React.useState(false);
    const [canScrollNext, setCanScrollNext] = React.useState(false);

    const onSelect = React.useCallback((api: CarouselApi) => {
      if (!api) {
        return;
      }

      setCanScrollPrev(api.canScrollPrev());
      setCanScrollNext(api.canScrollNext());
    }, []);

    const scrollPrev = React.useCallback(() => {
      console.log("scrolled prev");
      api?.scrollPrev();
    }, [api]);

    const scrollNext = React.useCallback(() => {
      console.log("scrolled next");
      api?.scrollNext();
    }, [api]);

    const handleKeyDown = React.useCallback(
      (event: React.KeyboardEvent<HTMLDivElement>) => {
        if (event.key === "ArrowLeft") {
          event.preventDefault();
          scrollPrev();
        } else if (event.key === "ArrowRight") {
          event.preventDefault();
          scrollNext();
        }
      },
      [scrollPrev, scrollNext]
    );

    React.useEffect(() => {
      if (!(api && setApi)) {
        return;
      }

      setApi(api);
    }, [api, setApi]);

    React.useEffect(() => {
      if (!api) {
        return;
      }

      onSelect(api);
      api.on("reInit", onSelect);
      api.on("select", onSelect);

      return () => {
        api?.off("select", onSelect);
      };
    }, [api, onSelect]);

    return (
      <CarouselContext.Provider
        value={{
          carouselRef,
          api,
          opts,
          orientation:
            orientation || (opts?.axis === "y" ? "vertical" : "horizontal"),
          scrollPrev,
          scrollNext,
          canScrollPrev,
          canScrollNext,
        }}
      >
        <div
          aria-roledescription="carousel"
          className={cn("relative", className)}
          onKeyDownCapture={handleKeyDown}
          ref={ref}
          role="region"
          {...props}
        >
          {children}
        </div>
      </CarouselContext.Provider>
    );
  }
);
Carousel.displayName = "Carousel";

const CarouselContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { carouselRef, orientation } = useCarousel();

  return (
    <div className="overflow-hidden" ref={carouselRef}>
      <div
        className={cn(
          "flex",
          orientation === "horizontal" ? "-ml-4" : "-mt-4 flex-col",
          className
        )}
        ref={ref}
        {...props}
      />
    </div>
  );
});
CarouselContent.displayName = "CarouselContent";

const CarouselItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { orientation } = useCarousel();

  return (
    <div
      aria-roledescription="slide"
      className={cn(
        "min-w-0 shrink-0 grow-0 basis-full",
        orientation === "horizontal" ? "pl-4" : "pt-4",
        className
      )}
      ref={ref}
      role="group"
      {...props}
    />
  );
});
CarouselItem.displayName = "CarouselItem";

const CarouselPrevious = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollPrev, canScrollPrev } = useCarousel();

  return (
    <Button
      className={cn(
        orientation === "horizontal"
          ? "-left-10 md:-left-14 -translate-y-1/2 active:-translate-y-1 top-1/2 h-9 w-8 md:h-10 md:w-9"
          : "-top-12 -translate-x-1/2 left-1/2 h-10 w-8 rotate-90 md:h-11 md:w-9",
        "absolute grid aspect-square place-items-center rounded-none",
        className
      )}
      disabled={!canScrollPrev}
      onClick={scrollPrev}
      ref={ref}
      size={size}
      variant={variant}
      {...props}
    >
      <svg
        aria-label="arrow-left"
        color="currentColor"
        fill="currentColor"
        height="50"
        stroke="currentColor"
        strokeWidth="0.25"
        viewBox="0 0 256 256"
        width="50"
        xmlns="http://www.w3.org/2000/svg"
      >
        <title>Previous</title>
        <rect height="14" rx="1" width="14" x="64" y="120" />
        <rect height="14" rx="1" width="14" x="96" y="120" />
        <rect height="14" rx="1" width="14" x="80" y="120" />
        <rect height="14" rx="1" width="14" x="112" y="120" />
        <rect height="14" rx="1" width="14" x="144" y="120" />
        <rect height="14" rx="1" width="14" x="160" y="120" />
        <rect height="14" rx="1" width="14" x="80" y="104" />
        <rect height="14" rx="1" width="14" x="96" y="88" />
        <rect height="14" rx="1" width="14" x="112" y="72" />
        <rect height="14" rx="1" width="14" x="80" y="136" />
        <rect height="14" rx="1" width="14" x="96" y="152" />
        <rect height="14" rx="1" width="14" x="112" y="168" />
        <rect height="14" rx="1" width="14" x="176" y="120" />
        <rect height="14" rx="1" width="14" x="128" y="120" />
      </svg>
      <span className="sr-only">Previous slide</span>
    </Button>
  );
});
CarouselPrevious.displayName = "CarouselPrevious";

const CarouselNext = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollNext, canScrollNext } = useCarousel();

  return (
    <Button
      className={cn(
        orientation === "horizontal"
          ? "-right-10 md:-right-14 -translate-y-1/2 active:-translate-y-1 top-1/2 aspect-square h-9 w-8 shrink-0 md:h-10 md:w-9"
          : "-bottom-12 -translate-x-1/2 left-1/2 h-10 w-8 rotate-90 md:h-11 md:w-9",
        "absolute grid aspect-square place-items-center rounded-none",
        className
      )}
      disabled={!canScrollNext}
      onClick={scrollNext}
      ref={ref}
      size={size}
      variant={variant}
      {...props}
    >
      <svg
        aria-label="arrow-right"
        className="block"
        color="currentColor"
        fill="currentColor"
        height="50"
        stroke="currentColor"
        strokeWidth="0.25"
        viewBox="0 0 256 256"
        width="50"
        xmlns="http://www.w3.org/2000/svg"
      >
        <title>Next</title>
        <rect height="14" rx="1" width="14" x="64" y="120" />
        <rect height="14" rx="1" width="14" x="96" y="120" />
        <rect height="14" rx="1" width="14" x="80" y="120" />
        <rect height="14" rx="1" width="14" x="112" y="120" />
        <rect height="14" rx="1" width="14" x="144" y="120" />
        <rect height="14" rx="1" width="14" x="160" y="120" />
        <rect height="14" rx="1" width="14" x="160" y="136" />
        <rect height="14" rx="1" width="14" x="144" y="152" />
        <rect height="14" rx="1" width="14" x="128" y="72" />
        <rect height="14" rx="1" width="14" x="128" y="168" />
        <rect height="14" rx="1" width="14" x="176" y="120" />
        <rect height="14" rx="1" width="14" x="160" y="104" />
        <rect height="14" rx="1" width="14" x="144" y="88" />
        <rect height="14" rx="1" width="14" x="128" y="120" />
      </svg>
      <span className="sr-only">Next slide</span>
    </Button>
  );
});
CarouselNext.displayName = "CarouselNext";

export {
  type CarouselApi,
  Carousel,
  CarouselContent,
  CarouselItem,
  CarouselPrevious,
  CarouselNext,
};

@import url("https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap");

.retro {
  font-family: "Press Start 2P", system-ui, -apple-system, sans-serif;
  line-height: 1.5;
  letter-spacing: 0.5px;
}
