import { Card, CardContent } from "@/components/ui/8bit/card";
import { cn } from "@/lib/utils";

const HEIGHT_CLASSES = {
  lg: "min-h-[420px] md:min-h-[640px]",
  sm: "min-h-[240px] md:min-h-[360px]",
  md: "min-h-[320px] md:min-h-[480px]",
};

const ALIGN_CLASSES = {
  left: "justify-start text-left",
  right: "justify-end text-right",
  center: "justify-center text-center",
};

export interface ChapterIntroProps extends React.ComponentProps<"div"> {
  title: string;
  subtitle?: string;
  backgroundSrc?: string;
  align?: "left" | "center" | "right";
  height?: "sm" | "md" | "lg";
  darken?: number;
}

export default function ChapterIntro({
  className,
  title,
  subtitle,
  backgroundSrc = "/placeholder.svg",
  align = "center",
  height = "md",
  darken = 0.5,
  ...props
}: ChapterIntroProps) {
  const heightClass = HEIGHT_CLASSES[height || "md"];
  const alignClass = ALIGN_CLASSES[align || "center"];

  return (
    <Card className={cn(className)} {...props}>
      <CardContent className={cn("relative p-0")}>
        <div className={cn("relative w-full", heightClass)}>
          {/* Background image */}
          <Image
            alt=""
            className="absolute inset-0 h-full w-full object-cover dark:brightness-[0.7]"
            fill
            src={backgroundSrc}
            style={{ imageRendering: "pixelated" }}
          />

          {/* Darken/gradient overlay for readability */}
          <div
            aria-hidden="true"
            className="absolute inset-0 bg-background/60 mix-blend-multiply"
            style={{ opacity: darken }}
          />

          {/* Cinematic letterbox bars */}
          <div
            aria-hidden="true"
            className="absolute top-0 right-0 left-0 h-6 bg-secondary/40 md:h-10"
          />
          <div
            aria-hidden="true"
            className="absolute right-0 bottom-0 left-0 h-6 bg-secondary/80 md:h-10"
          />

          {/* Content overlay */}
          <div
            className={cn(
              "relative z-10 flex h-full items-center p-8 md:p-12",
              alignClass
            )}
          >
            <div className="mx-auto max-w-3xl">
              <h1 className="font-bold text-2xl leading-tight drop-shadow-[0_3px_0_rgba(0,0,0,0.8)] md:text-4xl lg:text-5xl">
                {title}
              </h1>
              {subtitle && (
                <p className="mt-4 text-secondary/90 text-xs drop-shadow-[0_2px_0_rgba(0,0,0,0.8)] md:text-base dark:text-muted-foreground/90">
                  {subtitle}
                </p>
              )}
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

@import url("https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap");

.retro {
  font-family: "Press Start 2P", system-ui, -apple-system, sans-serif;
  line-height: 1.5;
  letter-spacing: 0.5px;
}

import { cva, type VariantProps } from "class-variance-authority";
import {
  Card as ShadcnCard,
  CardAction as ShadcnCardAction,
  CardContent as ShadcnCardContent,
  CardDescription as ShadcnCardDescription,
  CardFooter as ShadcnCardFooter,
  CardHeader as ShadcnCardHeader,
  CardTitle as ShadcnCardTitle,
} from "@/components/ui/card";
import { cn } from "@/lib/utils";

import "./styles/retro.css";

export const cardVariants = cva("", {
  variants: {
    font: {
      normal: "",
      retro: "retro",
    },
  },
  defaultVariants: {
    font: "retro",
  },
});

export interface BitCardProps
  extends React.ComponentProps<"div">,
    VariantProps<typeof cardVariants> {
  asChild?: boolean;
}

function Card({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <div
      className={cn(
        "!p-0 relative border-foreground border-y-6 dark:border-ring",
        className
      )}
    >
      <ShadcnCard
        {...props}
        className={cn(
          "!w-full rounded-none border-0",
          font !== "normal" && "retro",
          className
        )}
      />

      <div
        aria-hidden="true"
        className="-mx-1.5 pointer-events-none absolute inset-0 border-foreground border-x-6 dark:border-ring"
      />
    </div>
  );
}

function CardHeader({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardHeader
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardTitle({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardTitle
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardDescription({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardDescription
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardAction({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardAction
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardContent({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardContent
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardFooter({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardFooter
      className={cn(font !== "normal" && "retro", className)}
      data-slot="card-footer"
      {...props}
    />
  );
}

export {
  Card,
  CardHeader,
  CardFooter,
  CardTitle,
  CardAction,
  CardDescription,
  CardContent,
};
