import { cva, type VariantProps } from "class-variance-authority";
import HealthBar from "@/components/ui/8bit/health-bar";
import { cn } from "@/lib/utils";

import "./styles/retro.css";

export const enemyHealthDisplayVariants = cva("", {
  variants: {
    variant: {
      default: "",
      retro: "retro",
    },
    size: {
      sm: "text-xs",
      md: "text-sm",
      lg: "text-base",
    },
    textColor: {
      red: "text-red-500",
      orange: "text-orange-500",
      yellow: "text-yellow-500",
      green: "text-green-500",
      blue: "text-blue-500",
      purple: "text-purple-500",
    },
  },
  defaultVariants: {
    variant: "retro",
    size: "md",
    textColor: "red",
  },
});

export interface EnemyHealthDisplayProps
  extends React.ComponentProps<"div">,
    VariantProps<typeof enemyHealthDisplayVariants> {
  enemyName: string;
  level?: number;
  currentHealth: number;
  maxHealth: number;
  showLevel?: boolean;
  showHealthText?: boolean;
  healthBarVariant?: "retro" | "default";
  healthBarColor?: string;
  enemyNameColor?: string;
}

export default function EnemyHealthDisplay({
  className,
  variant,
  size,
  textColor,
  enemyName,
  level,
  currentHealth,
  maxHealth,
  showLevel = true,
  showHealthText = true,
  healthBarVariant = "retro",
  healthBarColor = "bg-red-500",
  enemyNameColor = "text-foreground",
  ...props
}: EnemyHealthDisplayProps) {
  const healthPercentage = Math.max(
    0,
    Math.min(100, (currentHealth / maxHealth) * 100)
  );
  const healthText = `${currentHealth}/${maxHealth}`;

  return (
    <div
      className={cn(
        "relative w-full space-y-2",
        enemyHealthDisplayVariants({ variant, size, textColor }),
        className
      )}
      {...props}
    >
      {/* Enemy Name and Level */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <span className={cn("font-bold", enemyNameColor)}>{enemyName}</span>
          {showLevel && level && (
            <span className="text-muted-foreground">Lv.{level}</span>
          )}
        </div>
        {showHealthText && (
          <span className="font-mono text-muted-foreground">{healthText}</span>
        )}
      </div>

      {/* Health Bar Container */}
      <div className="relative">
        <HealthBar
          className="w-full"
          props={{ progressBg: healthBarColor }}
          value={healthPercentage}
          variant={healthBarVariant}
        />

        {/* Health percentage overlay for retro variant */}
        {healthBarVariant === "retro" && (
          <div className="absolute inset-0 flex items-center justify-center">
            <span className="rounded bg-black/50 px-1 font-bold text-white text-xs drop-shadow-lg">
              {Math.round(healthPercentage)}%
            </span>
          </div>
        )}
      </div>
    </div>
  );
}

import { type BitProgressProps, Progress } from "@/components/ui/8bit/progress";

interface ManaBarProps extends React.ComponentProps<"div"> {
  className?: string;
  props?: BitProgressProps;
  variant?: "retro" | "default";
  value?: number;
}

export default function HealthBar({
  className,
  variant,
  value,
  ...props
}: ManaBarProps) {
  return (
    <Progress
      {...props}
      className={className}
      progressBg="bg-red-500"
      value={value}
      variant={variant}
    />
  );
}

import * as ProgressPrimitive from "@radix-ui/react-progress";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

import "./styles/retro.css";

export const progressVariants = cva("", {
  variants: {
    variant: {
      default: "",
      retro: "retro",
    },
    font: {
      normal: "",
      retro: "retro",
    },
  },
  defaultVariants: {
    font: "retro",
  },
});

export interface BitProgressProps
  extends React.ComponentProps<typeof ProgressPrimitive.Root>,
    VariantProps<typeof progressVariants> {
  className?: string;
  font?: VariantProps<typeof progressVariants>["font"];
  progressBg?: string;
}

function Progress({
  className,
  font,
  variant,
  value,
  progressBg,
  ...props
}: BitProgressProps) {
  // Extract height from className if present
  const heightMatch = className?.match(/h-(\d+|\[.*?\])/);
  const heightClass = heightMatch ? heightMatch[0] : "h-2";

  return (
    <div className={cn("relative w-full", className)}>
      <ProgressPrimitive.Root
        className={cn(
          "relative w-full overflow-hidden bg-primary/20",
          heightClass,
          font !== "normal" && "retro"
        )}
        data-slot="progress"
        {...props}
      >
        <ProgressPrimitive.Indicator
          className={cn(
            "h-full transition-all",
            variant === "retro" ? "flex" : "w-full flex-1",
            progressBg && variant !== "retro" ? progressBg : "bg-primary"
          )}
          data-slot="progress-indicator"
          style={
            variant === "retro"
              ? undefined
              : { transform: `translateX(-${100 - (value || 0)}%)` }
          }
        >
          {variant === "retro" && (
            <div className="flex w-full">
              {Array.from({ length: 20 }).map((_, i) => {
                const filledSquares = Math.round(((value || 0) / 100) * 20);
                return (
                  <div
                    className={cn(
                      "mx-[1px] size-full",
                      i < filledSquares ? progressBg : "bg-transparent"
                    )}
                    key={i}
                  />
                );
              })}
            </div>
          )}
        </ProgressPrimitive.Indicator>
      </ProgressPrimitive.Root>

      <div
        aria-hidden="true"
        className="-my-1 pointer-events-none absolute inset-0 border-foreground border-y-4 dark:border-ring"
      />

      <div
        aria-hidden="true"
        className="-mx-1 pointer-events-none absolute inset-0 border-foreground border-x-4 dark:border-ring"
      />
    </div>
  );
}

export { Progress };

@import url("https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap");

.retro {
  font-family: "Press Start 2P", system-ui, -apple-system, sans-serif;
  line-height: 1.5;
  letter-spacing: 0.5px;
}
