import { cva } from "class-variance-authority";
import * as React from "react";
import {
  Avatar,
  AvatarFallback,
  AvatarImage,
} from "@/components/ui/8bit/avatar";
import { Badge } from "@/components/ui/8bit/badge";
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from "@/components/ui/8bit/card";
import { Separator } from "@/components/ui/8bit/separator";
import { cn } from "@/lib/utils";

import "./styles/retro.css";

export type LeaderboardPlayer = {
  id: string;
  name: string;
  score: number;
  rank?: number;
  isCurrentPlayer?: boolean;
  avatar?: string;
  avatarFallback?: string;
};

export interface LeaderboardProps extends React.ComponentProps<"div"> {
  players: LeaderboardPlayer[];
  maxPlayers?: number;
  showRank?: boolean;
  showAvatar?: boolean;
  className?: string;
  title?: string;
  currentPlayerId?: string;
}

const playerItemVariants = cva(
  "flex items-center justify-between rounded-lg p-3 transition-all duration-200",
  {
    variants: {
      rank: {
        default: "bg-muted/50 hover:bg-muted",
        first:
          "border-2 border-yellow-400 bg-gradient-to-r from-yellow-400/20 to-yellow-600/20 hover:from-yellow-400/30 hover:to-yellow-600/30",
        second:
          "border-2 border-gray-400 bg-gradient-to-r from-gray-300/20 to-gray-500/20 hover:from-gray-300/30 hover:to-gray-500/30",
        third:
          "border-2 border-amber-600 bg-gradient-to-r from-amber-600/20 to-amber-800/20 hover:from-amber-600/30 hover:to-amber-800/30",
        current: "border-2 border-primary bg-primary/20 hover:bg-primary/30",
      },
    },
    defaultVariants: {
      rank: "default",
    },
  }
);

const rankBadgeVariants = cva(
  "flex size-8 items-center justify-center font-bold text-sm",
  {
    variants: {
      rank: {
        default: "bg-muted text-muted-foreground",
        first:
          "bg-gradient-to-br from-yellow-400 to-yellow-600 text-yellow-900 shadow-lg",
        second:
          "bg-gradient-to-br from-gray-300 to-gray-500 text-gray-900 shadow-lg",
        third:
          "bg-gradient-to-br from-amber-600 to-amber-800 text-amber-100 shadow-lg",
        current: "bg-primary text-primary-foreground",
      },
    },
    defaultVariants: {
      rank: "default",
    },
  }
);

function getRankVariant(
  rank: number,
  isCurrentPlayer: boolean
): "default" | "first" | "second" | "third" | "current" {
  if (isCurrentPlayer) {
    return "current";
  }
  if (rank === 1) {
    return "first";
  }
  if (rank === 2) {
    return "second";
  }
  if (rank === 3) {
    return "third";
  }
  return "default";
}

function formatScore(score: number): string {
  return score.toLocaleString();
}

function getRankIcon(rank: number): string {
  switch (rank) {
    case 1:
      return "ðŸ¥‡";
    case 2:
      return "ðŸ¥ˆ";
    case 3:
      return "ðŸ¥‰";
    default:
      return rank.toString();
  }
}

export function Leaderboard({
  players,
  maxPlayers = 10,
  showRank = true,
  showAvatar = true,
  className,
  title = "LEADERBOARD",
  currentPlayerId,
  ...props
}: LeaderboardProps) {
  // Sort players by score (descending) and assign ranks
  const sortedPlayers = React.useMemo(
    () =>
      players
        .sort((a, b) => b.score - a.score)
        .slice(0, maxPlayers)
        .map((player, index) => ({
          ...player,
          rank: index + 1,
          isCurrentPlayer: currentPlayerId
            ? player.id === currentPlayerId
            : player.isCurrentPlayer,
        })),
    [players, maxPlayers, currentPlayerId]
  );

  return (
    <Card
      className={className}
      data-slot="leaderboard"
      font={"retro"}
      {...props}
    >
      {title && (
        <CardHeader>
          <CardTitle className="text-center">{title}</CardTitle>
        </CardHeader>
      )}

      <CardContent className="space-y-5">
        <div className="space-y-2">
          {sortedPlayers.length === 0 ? (
            <div className="py-8 text-center text-muted-foreground">
              <p className="retro text-sm">No players yet</p>
            </div>
          ) : (
            sortedPlayers.map((player) => {
              const rankVariant = getRankVariant(
                player.rank!,
                player.isCurrentPlayer!
              );

              return (
                <div
                  className={cn(
                    playerItemVariants({ rank: rankVariant }),
                    "retro"
                  )}
                  key={player.id}
                >
                  <div className="flex items-center gap-3">
                    {showAvatar && (
                      <Avatar className="size-10" font="retro" variant="pixel">
                        {player.avatar && (
                          <AvatarImage alt={player.name} src={player.avatar} />
                        )}
                        <AvatarFallback className="retro text-xs">
                          {player.avatarFallback ||
                            player.name.charAt(0).toUpperCase()}
                        </AvatarFallback>
                      </Avatar>
                    )}

                    {showRank && !showAvatar && (
                      <div
                        className={cn(rankBadgeVariants({ rank: rankVariant }))}
                      >
                        <span className="text-xs">
                          {getRankIcon(player.rank!)}
                        </span>
                      </div>
                    )}

                    <div className="min-w-0 flex-1">
                      <div className="flex items-center gap-4">
                        <span
                          className={cn(
                            "retro truncate font-medium text-xs md:text-sm",
                            player.isCurrentPlayer && "font-bold text-primary"
                          )}
                        >
                          {player.name}
                        </span>
                        {player.isCurrentPlayer && (
                          <Badge className="text-[9px]">YOU</Badge>
                        )}
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center gap-2">
                    <span
                      className={cn(
                        "retro font-bold text-xs md:text-sm",
                        rankVariant === "first" && "text-yellow-600",
                        rankVariant === "second" && "text-gray-600",
                        rankVariant === "third" && "text-amber-700",
                        player.isCurrentPlayer && "text-primary"
                      )}
                    >
                      {formatScore(player.score)}
                    </span>
                  </div>
                </div>
              );
            })
          )}
        </div>

        <Separator />

        {sortedPlayers.length > 0 && (
          <div className="mt-4 pt-4">
            <p
              className={cn("retro text-center text-muted-foreground text-xs")}
            >
              Showing top {Math.min(sortedPlayers.length, maxPlayers)} players
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

export default Leaderboard;

@import url("https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap");

.retro {
  font-family: "Press Start 2P", system-ui, -apple-system, sans-serif;
  line-height: 1.5;
  letter-spacing: 0.5px;
}

import { cva, type VariantProps } from "class-variance-authority";
import {
  Card as ShadcnCard,
  CardAction as ShadcnCardAction,
  CardContent as ShadcnCardContent,
  CardDescription as ShadcnCardDescription,
  CardFooter as ShadcnCardFooter,
  CardHeader as ShadcnCardHeader,
  CardTitle as ShadcnCardTitle,
} from "@/components/ui/card";
import { cn } from "@/lib/utils";

import "./styles/retro.css";

export const cardVariants = cva("", {
  variants: {
    font: {
      normal: "",
      retro: "retro",
    },
  },
  defaultVariants: {
    font: "retro",
  },
});

export interface BitCardProps
  extends React.ComponentProps<"div">,
    VariantProps<typeof cardVariants> {
  asChild?: boolean;
}

function Card({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <div
      className={cn(
        "!p-0 relative border-foreground border-y-6 dark:border-ring",
        className
      )}
    >
      <ShadcnCard
        {...props}
        className={cn(
          "!w-full rounded-none border-0",
          font !== "normal" && "retro",
          className
        )}
      />

      <div
        aria-hidden="true"
        className="-mx-1.5 pointer-events-none absolute inset-0 border-foreground border-x-6 dark:border-ring"
      />
    </div>
  );
}

function CardHeader({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardHeader
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardTitle({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardTitle
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardDescription({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardDescription
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardAction({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardAction
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardContent({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardContent
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardFooter({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardFooter
      className={cn(font !== "normal" && "retro", className)}
      data-slot="card-footer"
      {...props}
    />
  );
}

export {
  Card,
  CardHeader,
  CardFooter,
  CardTitle,
  CardAction,
  CardDescription,
  CardContent,
};

import * as AvatarPrimitive from "@radix-ui/react-avatar";
import { cva } from "class-variance-authority";
import type React from "react";
import { forwardRef } from "react";

import { cn } from "@/lib/utils";

import "./styles/retro.css";

export const avatarVariants = cva("", {
  variants: {
    font: {
      normal: "",
      retro: "retro",
    },
    variant: {
      default: "",
      retro: "",
      pixel: "",
    },
  },
  defaultVariants: {
    font: "retro",
    variant: "pixel",
  },
});

const Avatar = forwardRef<
  React.ComponentRef<typeof AvatarPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof AvatarPrimitive.Root> & {
    font?: "normal" | "retro";
    variant?: "default" | "retro" | "pixel";
  }
>(({ className = "", font, variant = "pixel", ...props }, ref) => {
  const isPixel = variant === "pixel";

  return (
    <div className={cn("relative size-max", className)}>
      {/* Pixel frame (only show if pixel variant) */}
      {isPixel && (
        <div
          className="pointer-events-none absolute inset-0 h-full w-full"
          style={{ zIndex: 10 }}
        >
          {/* Top section - Row 1 */}
          <div className="absolute top-0 right-[23%] left-[23%] h-[7%] bg-foreground dark:bg-ring" />

          {/* Top section - Row 2 */}
          <div className="absolute top-[6.25%] right-[17%] left-[17%] h-[7%] bg-foreground dark:bg-ring" />

          {/* Top section - Row 3 */}
          <div className="absolute top-[12.5%] left-[11%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[12.5%] right-[11%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />

          {/* Top section - Row 4 */}
          <div className="absolute top-[18.75%] left-[5%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[18.75%] right-[5%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />

          {/* Top section - Row 5 */}
          <div className="absolute top-[25%] left-0 h-[7%] w-[20%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[25%] right-0 h-[7%] w-[20%] bg-foreground dark:bg-ring" />

          {/* Top section - Rows 6-7 */}
          <div className="absolute top-[31.25%] left-0 h-[13%] w-[13.5%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[31.25%] right-0 h-[13%] w-[13.5%] bg-foreground dark:bg-ring" />

          {/* Top section - Rows 8-10 */}
          <div className="absolute top-[43.75%] left-0 h-[7%] w-[13.5%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[43.75%] right-0 h-[7%] w-[13.5%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Rows 8-10 (mirror) */}
          <div className="absolute top-[50%] left-0 h-[7%] w-[13.5%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[50%] right-0 h-[7%] w-[13.5%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Rows 6-7 (mirror) */}
          <div className="absolute top-[56.25%] left-0 h-[13%] w-[13.5%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[56.25%] right-0 h-[13%] w-[13.5%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Row 5 (mirror) */}
          <div className="absolute top-[68.75%] left-0 h-[7%] w-[20%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[68.75%] right-0 h-[7%] w-[20%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Row 4 (mirror) */}
          <div className="absolute top-[75%] left-[5%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[75%] right-[5%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Row 3 (mirror) */}
          <div className="absolute top-[81.25%] left-[11%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[81.25%] right-[11%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Row 2 (mirror) */}
          <div className="absolute top-[87.5%] right-[17%] left-[17%] h-[7%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Row 1 (mirror) */}
          <div className="absolute right-[23%] bottom-0 left-[23%] h-[7%] bg-foreground dark:bg-ring" />
        </div>
      )}

      <AvatarPrimitive.Root
        className={cn(
          "relative flex size-10 shrink-0 overflow-hidden text-xs",
          !isPixel && "rounded-none",
          isPixel && "rounded-full",
          font !== "normal" && "retro",
          variant === "retro" && "image-rendering-pixelated",
          className
        )}
        data-slot="avatar"
        ref={ref}
        {...props}
      />

      {/* Original border styling (only show if not pixel variant) */}
      {!isPixel && (
        <>
          <div className="pointer-events-none absolute top-0 left-0 h-1.5 w-full bg-foreground dark:bg-ring" />
          <div className="pointer-events-none absolute bottom-0 h-1.5 w-full bg-foreground dark:bg-ring" />
          <div className="-left-1.5 pointer-events-none absolute top-1.5 h-1/2 w-1.5 bg-foreground dark:bg-ring" />
          <div className="-left-1.5 pointer-events-none absolute bottom-1.5 h-1/2 w-1.5 bg-foreground dark:bg-ring" />
          <div className="-right-1.5 pointer-events-none absolute top-1.5 h-1/2 w-1.5 bg-foreground dark:bg-ring" />
          <div className="-right-1.5 pointer-events-none absolute bottom-1.5 h-1/2 w-1.5 bg-foreground dark:bg-ring" />
        </>
      )}
    </div>
  );
});
Avatar.displayName = AvatarPrimitive.Root.displayName;

interface BitAvatarImageProps
  extends React.ComponentPropsWithoutRef<typeof AvatarPrimitive.Image> {
  font?: "normal" | "retro";
  variant?: "default" | "retro" | "pixel";
}

const AvatarImage = forwardRef<
  React.ComponentRef<typeof AvatarPrimitive.Image>,
  BitAvatarImageProps
>(({ className, font, ...props }, ref) => (
  <AvatarPrimitive.Image
    className={cn(
      "aspect-square h-full w-full",
      font === "retro" && "retro",
      className
    )}
    data-slot="avatar-image"
    ref={ref}
    {...props}
  />
));
AvatarImage.displayName = AvatarPrimitive.Image.displayName;

const AvatarFallback = forwardRef<
  React.ComponentRef<typeof AvatarPrimitive.Fallback>,
  React.ComponentPropsWithoutRef<typeof AvatarPrimitive.Fallback>
>(({ className, ...props }, ref) => (
  <AvatarPrimitive.Fallback
    className={cn(
      "flex h-full w-full items-center justify-center rounded-full bg-muted text-foreground",
      className
    )}
    data-slot="avatar-fallback"
    ref={ref}
    {...props}
  />
));
AvatarFallback.displayName = AvatarPrimitive.Fallback.displayName;

export { Avatar, AvatarImage, AvatarFallback };

import { cva, type VariantProps } from "class-variance-authority";
import { Badge as ShadcnBadge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";

export const badgeVariants = cva("", {
  variants: {
    font: {
      normal: "",
      retro: "retro",
    },
    variant: {
      default: "border-primary bg-primary",
      destructive: "border-destructive bg-destructive",
      outline: "border-background bg-background",
      secondary: "border-secondary bg-secondary",
    },
  },
  defaultVariants: {
    variant: "default",
  },
});

export interface BitButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof badgeVariants> {
  asChild?: boolean;
}

function Badge({
  children,
  className = "",
  font,
  variant,
  ...props
}: BitButtonProps) {
  const color = badgeVariants({ variant, font });

  const classes = className.split(" ");

  // visual classes for badge and sidebars
  const visualClasses = classes.filter(
    (c) =>
      c.startsWith("bg-") ||
      c.startsWith("border-") ||
      c.startsWith("text-") ||
      c.startsWith("rounded-")
  );

  // Container should accept all non-visual utility classes (e.g., size, spacing, layout)
  const containerClasses = classes.filter(
    (c) =>
      !(
        c.startsWith("bg-") ||
        c.startsWith("border-") ||
        c.startsWith("text-") ||
        c.startsWith("rounded-")
      )
  );

  return (
    <div className={cn("relative inline-flex items-stretch", containerClasses)}>
      <ShadcnBadge
        {...props}
        className={cn(
          "h-full",
          "rounded-none",
          "w-full",
          font !== "normal" && "retro",
          visualClasses
        )}
        variant={variant}
      >
        {children}
      </ShadcnBadge>

      {/* Left pixel bar */}
      <div
        className={cn(
          "-left-1.5 absolute inset-y-[4px] w-1.5",
          color,
          visualClasses
        )}
      />
      {/* Right pixel bar */}
      <div
        className={cn(
          "-right-1.5 absolute inset-y-[4px] w-1.5",
          color,
          visualClasses
        )}
      />
    </div>
  );
}

export { Badge };

"use client";

import * as SeparatorPrimitive from "@radix-ui/react-separator";
import type * as React from "react";

import { cn } from "@/lib/utils";

function Separator({
  className,
  orientation = "horizontal",
  decorative = true,
  ...props
}: React.ComponentProps<typeof SeparatorPrimitive.Root>) {
  return (
    <SeparatorPrimitive.Root
      className={cn(
        "shrink-0 data-[orientation=horizontal]:h-0.5 data-[orientation=vertical]:h-full data-[orientation=horizontal]:w-full data-[orientation=vertical]:w-0.5 data-[orientation=horizontal]:bg-[length:16px_8px] data-[orientation=horizontal]:bg-[linear-gradient(90deg,var(--foreground)_75%,transparent_75%)] data-[orientation=vertical]:bg-[length:2px_16px] data-[orientation=vertical]:bg-[linear-gradient(0deg,var(--foreground)_75%,transparent_75%)] dark:data-[orientation=horizontal]:bg-[linear-gradient(90deg,var(--ring)_75%,transparent_75%)] dark:data-[orientation=vertical]:bg-[linear-gradient(0deg,var(--ring)_75%,transparent_75%)]",
        className
      )}
      data-slot="separator-root"
      decorative={decorative}
      orientation={orientation}
      {...props}
    />
  );
}

export { Separator };
