import { Button } from "@/components/ui/8bit/button";
import {
	Card,
	CardContent,
	CardDescription,
	CardHeader,
	CardTitle,
} from "@/components/ui/8bit/card";
import { cn } from "@/lib/utils";

const menuItems = [
	{
		label: "START GAME",
		action: () => console.log("Starting game..."),
	},
	{
		label: "OPTIONS",
		action: () => console.log("Showing options..."),
	},
	{
		label: "HIGH SCORES",
		action: () => console.log("Showing high scores..."),
	},
	{
		label: "MULTIPLAYER",
		action: () => console.log("Multiplayer mode..."),
	},
	{ label: "QUIT", action: () => console.log("Quitting game...") },
];

export default function MainMenu({
	className,
	...props
}: React.ComponentProps<"div">) {
	return (
		<Card className={cn(className)} {...props}>
			<CardHeader className="flex flex-col items-center justify-center gap-2">
				<CardTitle>Main Menu</CardTitle>
				<CardDescription>Retro 8-bit Quest</CardDescription>
			</CardHeader>
			<CardContent>
				<div className="flex flex-col gap-8">
					{menuItems.map((item) => (
						<Button key={item.label} className="flex items-center gap-2">
							<span>{item.label}</span>
						</Button>
					))}
				</div>
			</CardContent>
		</Card>
	);
}

@import url("https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap");

.retro {
	font-family: "Press Start 2P", system-ui, -apple-system, sans-serif;
	line-height: 1.5;
	letter-spacing: 0.5px;
}

import { cva, type VariantProps } from "class-variance-authority";
import { Button as ShadcnButton } from "@/components/ui/button";
import { cn } from "@/lib/utils";

import "./styles/retro.css";

export const buttonVariants = cva("", {
	variants: {
		font: {
			normal: "",
			retro: "retro",
		},
		variant: {
			default: "bg-foreground",
			destructive: "bg-foreground",
			outline: "bg-foreground",
			secondary: "bg-secondary text-secondary-foreground hover:bg-secondary/80",
			ghost: "hover:bg-accent hover:text-accent-foreground",
			link: "text-primary underline-offset-4 hover:underline",
		},
		size: {
			default: "h-9 px-4 py-2 has-[>svg]:px-3",
			sm: "h-8 rounded-md gap-1.5 px-3 has-[>svg]:px-2.5",
			lg: "h-10 rounded-md px-6 has-[>svg]:px-4",
			icon: "size-9",
		},
	},
	defaultVariants: {
		variant: "default",
		size: "default",
	},
});

export interface BitButtonProps
	extends React.ButtonHTMLAttributes<HTMLButtonElement>,
		VariantProps<typeof buttonVariants> {
	asChild?: boolean;
	ref?: React.Ref<HTMLButtonElement>;
}

function Button({ children, asChild, ...props }: BitButtonProps) {
	const { variant, size, className, font } = props;

	return (
		<ShadcnButton
			{...props}
			className={cn(
				"rounded-none active:translate-y-1 transition-transform relative inline-flex items-center justify-center gap-1.5 border-none",
				font !== "normal" && "retro",
				className,
			)}
			size={size}
			variant={variant}
			asChild={asChild}
		>
			{asChild ? (
				<span className="relative inline-flex items-center justify-center gap-1.5">
					{children}

					{variant !== "ghost" && variant !== "link" && size !== "icon" && (
						<>
							{/* Pixelated border */}
							<div className="absolute -top-1.5 w-1/2 left-1.5 h-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute -top-1.5 w-1/2 right-1.5 h-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute -bottom-1.5 w-1/2 left-1.5 h-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute -bottom-1.5 w-1/2 right-1.5 h-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute top-0 left-0 size-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute top-0 right-0 size-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute bottom-0 left-0 size-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute bottom-0 right-0 size-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute top-1.5 -left-1.5 h-[calc(100%-12px)] w-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute top-1.5 -right-1.5 h-[calc(100%-12px)] w-1.5 bg-foreground dark:bg-ring" />
							{variant !== "outline" && (
								<>
									{/* Top shadow */}
									<div className="absolute top-0 left-0 w-full h-1.5 bg-foreground/20" />
									<div className="absolute top-1.5 left-0 w-3 h-1.5 bg-foreground/20" />

									{/* Bottom shadow */}
									<div className="absolute bottom-0 left-0 w-full h-1.5 bg-foreground/20" />
									<div className="absolute bottom-1.5 right-0 w-3 h-1.5 bg-foreground/20" />
								</>
							)}
						</>
					)}

					{size === "icon" && (
						<>
							<div className="absolute top-0 left-0 w-full h-[5px] md:h-1.5 bg-foreground dark:bg-ring pointer-events-none" />
							<div className="absolute bottom-0 w-full h-[5px] md:h-1.5 bg-foreground dark:bg-ring pointer-events-none" />
							<div className="absolute top-1 -left-1 w-[5px] md:w-1.5 h-1/2 bg-foreground dark:bg-ring pointer-events-none" />
							<div className="absolute bottom-1 -left-1 w-[5px] md:w-1.5 h-1/2 bg-foreground dark:bg-ring pointer-events-none" />
							<div className="absolute top-1 -right-1 w-[5px] md:w-1.5 h-1/2 bg-foreground dark:bg-ring pointer-events-none" />
							<div className="absolute bottom-1 -right-1 w-[5px] md:w-1.5 h-1/2 bg-foreground dark:bg-ring pointer-events-none" />
						</>
					)}
				</span>
			) : (
				<>
					{children}

					{variant !== "ghost" && variant !== "link" && size !== "icon" && (
						<>
							{/* Pixelated border */}
							<div className="absolute -top-1.5 w-1/2 left-1.5 h-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute -top-1.5 w-1/2 right-1.5 h-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute -bottom-1.5 w-1/2 left-1.5 h-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute -bottom-1.5 w-1/2 right-1.5 h-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute top-0 left-0 size-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute top-0 right-0 size-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute bottom-0 left-0 size-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute bottom-0 right-0 size-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute top-1.5 -left-1.5 h-[calc(100%-12px)] w-1.5 bg-foreground dark:bg-ring" />
							<div className="absolute top-1.5 -right-1.5 h-[calc(100%-12px)] w-1.5 bg-foreground dark:bg-ring" />
							{variant !== "outline" && (
								<>
									{/* Top shadow */}
									<div className="absolute top-0 left-0 w-full h-1.5 bg-foreground/20" />
									<div className="absolute top-1.5 left-0 w-3 h-1.5 bg-foreground/20" />

									{/* Bottom shadow */}
									<div className="absolute bottom-0 left-0 w-full h-1.5 bg-foreground/20" />
									<div className="absolute bottom-1.5 right-0 w-3 h-1.5 bg-foreground/20" />
								</>
							)}
						</>
					)}

					{size === "icon" && (
						<>
							<div className="absolute top-0 left-0 w-full h-[5px] md:h-1.5 bg-foreground dark:bg-ring pointer-events-none" />
							<div className="absolute bottom-0 w-full h-[5px] md:h-1.5 bg-foreground dark:bg-ring pointer-events-none" />
							<div className="absolute top-1 -left-1 w-[5px] md:w-1.5 h-1/2 bg-foreground dark:bg-ring pointer-events-none" />
							<div className="absolute bottom-1 -left-1 w-[5px] md:w-1.5 h-1/2 bg-foreground dark:bg-ring pointer-events-none" />
							<div className="absolute top-1 -right-1 w-[5px] md:w-1.5 h-1/2 bg-foreground dark:bg-ring pointer-events-none" />
							<div className="absolute bottom-1 -right-1 w-[5px] md:w-1.5 h-1/2 bg-foreground dark:bg-ring pointer-events-none" />
						</>
					)}
				</>
			)}
		</ShadcnButton>
	);
}

export { Button };

import { cva, type VariantProps } from "class-variance-authority";
import {
	Card as ShadcnCard,
	CardAction as ShadcnCardAction,
	CardContent as ShadcnCardContent,
	CardDescription as ShadcnCardDescription,
	CardFooter as ShadcnCardFooter,
	CardHeader as ShadcnCardHeader,
	CardTitle as ShadcnCardTitle,
} from "@/components/ui/card";
import { cn } from "@/lib/utils";

import "./styles/retro.css";

export const cardVariants = cva("", {
	variants: {
		font: {
			normal: "",
			retro: "retro",
		},
	},
	defaultVariants: {
		font: "retro",
	},
});

export interface BitCardProps
	extends React.ComponentProps<"div">,
		VariantProps<typeof cardVariants> {
	asChild?: boolean;
}

function Card({ ...props }: BitCardProps) {
	const { className, font } = props;

	return (
		<div
			className={cn(
				"relative border-y-6 border-foreground dark:border-ring !p-0",
				className,
			)}
		>
			<ShadcnCard
				{...props}
				className={cn(
					"rounded-none border-0 !w-full",
					font !== "normal" && "retro",
					className,
				)}
			/>

			<div
				className="absolute inset-0 border-x-6 -mx-1.5 border-foreground dark:border-ring pointer-events-none"
				aria-hidden="true"
			/>
		</div>
	);
}

function CardHeader({ ...props }: BitCardProps) {
	const { className, font } = props;

	return (
		<ShadcnCardHeader
			className={cn(font !== "normal" && "retro", className)}
			{...props}
		/>
	);
}

function CardTitle({ ...props }: BitCardProps) {
	const { className, font } = props;

	return (
		<ShadcnCardTitle
			className={cn(font !== "normal" && "retro", className)}
			{...props}
		/>
	);
}

function CardDescription({ ...props }: BitCardProps) {
	const { className, font } = props;

	return (
		<ShadcnCardDescription
			className={cn(font !== "normal" && "retro", className)}
			{...props}
		/>
	);
}

function CardAction({ ...props }: BitCardProps) {
	const { className, font } = props;

	return (
		<ShadcnCardAction
			className={cn(font !== "normal" && "retro", className)}
			{...props}
		/>
	);
}

function CardContent({ ...props }: BitCardProps) {
	const { className, font } = props;

	return (
		<ShadcnCardContent
			className={cn(font !== "normal" && "retro", className)}
			{...props}
		/>
	);
}

function CardFooter({ ...props }: BitCardProps) {
	const { className, font } = props;

	return (
		<ShadcnCardFooter
			data-slot="card-footer"
			className={cn(font !== "normal" && "retro", className)}
			{...props}
		/>
	);
}

export {
	Card,
	CardHeader,
	CardFooter,
	CardTitle,
	CardAction,
	CardDescription,
	CardContent,
};
