import { cva, type VariantProps } from "class-variance-authority";
import {
  Card as ShadcnCard,
  CardAction as ShadcnCardAction,
  CardContent as ShadcnCardContent,
  CardDescription as ShadcnCardDescription,
  CardFooter as ShadcnCardFooter,
  CardHeader as ShadcnCardHeader,
  CardTitle as ShadcnCardTitle,
} from "@/components/ui/card";
import { cn } from "@/lib/utils";

import "./styles/retro.css";

export const cardVariants = cva("", {
  variants: {
    font: {
      normal: "",
      retro: "retro",
    },
  },
  defaultVariants: {
    font: "retro",
  },
});

export interface BitCardProps
  extends React.ComponentProps<"div">,
    VariantProps<typeof cardVariants> {
  asChild?: boolean;
}

function Card({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <div
      className={cn(
        "!p-0 relative border-foreground border-y-6 dark:border-ring",
        className
      )}
    >
      <ShadcnCard
        {...props}
        className={cn(
          "!w-full rounded-none border-0",
          font !== "normal" && "retro",
          className
        )}
      />

      <div
        aria-hidden="true"
        className="-mx-1.5 pointer-events-none absolute inset-0 border-foreground border-x-6 dark:border-ring"
      />
    </div>
  );
}

function CardHeader({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardHeader
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardTitle({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardTitle
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardDescription({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardDescription
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardAction({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardAction
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardContent({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardContent
      className={cn(font !== "normal" && "retro", className)}
      {...props}
    />
  );
}

function CardFooter({ ...props }: BitCardProps) {
  const { className, font } = props;

  return (
    <ShadcnCardFooter
      className={cn(font !== "normal" && "retro", className)}
      data-slot="card-footer"
      {...props}
    />
  );
}

export {
  Card,
  CardHeader,
  CardFooter,
  CardTitle,
  CardAction,
  CardDescription,
  CardContent,
};

import { cva, type VariantProps } from "class-variance-authority";
import { Button as ShadcnButton } from "@/components/ui/button";
import { cn } from "@/lib/utils";

import "./styles/retro.css";

export const buttonVariants = cva("", {
  variants: {
    font: {
      normal: "",
      retro: "retro",
    },
    variant: {
      default: "bg-foreground",
      destructive: "bg-foreground",
      outline: "bg-foreground",
      secondary: "bg-secondary text-secondary-foreground hover:bg-secondary/80",
      ghost: "hover:bg-accent hover:text-accent-foreground",
      link: "text-primary underline-offset-4 hover:underline",
    },
    size: {
      default: "h-9 px-4 py-2 has-[>svg]:px-3",
      sm: "h-8 gap-1.5 rounded-md px-3 has-[>svg]:px-2.5",
      lg: "h-10 rounded-md px-6 has-[>svg]:px-4",
      icon: "size-9",
    },
  },
  defaultVariants: {
    variant: "default",
    size: "default",
  },
});

export interface BitButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
  ref?: React.Ref<HTMLButtonElement>;
}

function Button({ children, asChild, ...props }: BitButtonProps) {
  const { variant, size, className, font } = props;

  return (
    <ShadcnButton
      {...props}
      asChild={asChild}
      className={cn(
        "relative inline-flex items-center justify-center gap-1.5 rounded-none border-none transition-transform active:translate-y-1",
        font !== "normal" && "retro",
        className
      )}
      size={size}
      variant={variant}
    >
      {asChild ? (
        <span className="relative inline-flex items-center justify-center gap-1.5">
          {children}

          {variant !== "ghost" && variant !== "link" && size !== "icon" && (
            <>
              {/* Pixelated border */}
              <div className="-top-1.5 absolute left-1.5 h-1.5 w-1/2 bg-foreground dark:bg-ring" />
              <div className="-top-1.5 absolute right-1.5 h-1.5 w-1/2 bg-foreground dark:bg-ring" />
              <div className="-bottom-1.5 absolute left-1.5 h-1.5 w-1/2 bg-foreground dark:bg-ring" />
              <div className="-bottom-1.5 absolute right-1.5 h-1.5 w-1/2 bg-foreground dark:bg-ring" />
              <div className="absolute top-0 left-0 size-1.5 bg-foreground dark:bg-ring" />
              <div className="absolute top-0 right-0 size-1.5 bg-foreground dark:bg-ring" />
              <div className="absolute bottom-0 left-0 size-1.5 bg-foreground dark:bg-ring" />
              <div className="absolute right-0 bottom-0 size-1.5 bg-foreground dark:bg-ring" />
              <div className="-left-1.5 absolute top-1.5 h-[calc(100%-12px)] w-1.5 bg-foreground dark:bg-ring" />
              <div className="-right-1.5 absolute top-1.5 h-[calc(100%-12px)] w-1.5 bg-foreground dark:bg-ring" />
              {variant !== "outline" && (
                <>
                  {/* Top shadow */}
                  <div className="absolute top-0 left-0 h-1.5 w-full bg-foreground/20" />
                  <div className="absolute top-1.5 left-0 h-1.5 w-3 bg-foreground/20" />

                  {/* Bottom shadow */}
                  <div className="absolute bottom-0 left-0 h-1.5 w-full bg-foreground/20" />
                  <div className="absolute right-0 bottom-1.5 h-1.5 w-3 bg-foreground/20" />
                </>
              )}
            </>
          )}

          {size === "icon" && (
            <>
              <div className="pointer-events-none absolute top-0 left-0 h-[5px] w-full bg-foreground md:h-1.5 dark:bg-ring" />
              <div className="pointer-events-none absolute bottom-0 h-[5px] w-full bg-foreground md:h-1.5 dark:bg-ring" />
              <div className="-left-1 pointer-events-none absolute top-1 h-1/2 w-[5px] bg-foreground md:w-1.5 dark:bg-ring" />
              <div className="-left-1 pointer-events-none absolute bottom-1 h-1/2 w-[5px] bg-foreground md:w-1.5 dark:bg-ring" />
              <div className="-right-1 pointer-events-none absolute top-1 h-1/2 w-[5px] bg-foreground md:w-1.5 dark:bg-ring" />
              <div className="-right-1 pointer-events-none absolute bottom-1 h-1/2 w-[5px] bg-foreground md:w-1.5 dark:bg-ring" />
            </>
          )}
        </span>
      ) : (
        <>
          {children}

          {variant !== "ghost" && variant !== "link" && size !== "icon" && (
            <>
              {/* Pixelated border */}
              <div className="-top-1.5 absolute left-1.5 h-1.5 w-1/2 bg-foreground dark:bg-ring" />
              <div className="-top-1.5 absolute right-1.5 h-1.5 w-1/2 bg-foreground dark:bg-ring" />
              <div className="-bottom-1.5 absolute left-1.5 h-1.5 w-1/2 bg-foreground dark:bg-ring" />
              <div className="-bottom-1.5 absolute right-1.5 h-1.5 w-1/2 bg-foreground dark:bg-ring" />
              <div className="absolute top-0 left-0 size-1.5 bg-foreground dark:bg-ring" />
              <div className="absolute top-0 right-0 size-1.5 bg-foreground dark:bg-ring" />
              <div className="absolute bottom-0 left-0 size-1.5 bg-foreground dark:bg-ring" />
              <div className="absolute right-0 bottom-0 size-1.5 bg-foreground dark:bg-ring" />
              <div className="-left-1.5 absolute top-1.5 h-[calc(100%-12px)] w-1.5 bg-foreground dark:bg-ring" />
              <div className="-right-1.5 absolute top-1.5 h-[calc(100%-12px)] w-1.5 bg-foreground dark:bg-ring" />
              {variant !== "outline" && (
                <>
                  {/* Top shadow */}
                  <div className="absolute top-0 left-0 h-1.5 w-full bg-foreground/20" />
                  <div className="absolute top-1.5 left-0 h-1.5 w-3 bg-foreground/20" />

                  {/* Bottom shadow */}
                  <div className="absolute bottom-0 left-0 h-1.5 w-full bg-foreground/20" />
                  <div className="absolute right-0 bottom-1.5 h-1.5 w-3 bg-foreground/20" />
                </>
              )}
            </>
          )}

          {size === "icon" && (
            <>
              <div className="pointer-events-none absolute top-0 left-0 h-[5px] w-full bg-foreground md:h-1.5 dark:bg-ring" />
              <div className="pointer-events-none absolute bottom-0 h-[5px] w-full bg-foreground md:h-1.5 dark:bg-ring" />
              <div className="-left-1 pointer-events-none absolute top-1 h-1/2 w-[5px] bg-foreground md:w-1.5 dark:bg-ring" />
              <div className="-left-1 pointer-events-none absolute bottom-1 h-1/2 w-[5px] bg-foreground md:w-1.5 dark:bg-ring" />
              <div className="-right-1 pointer-events-none absolute top-1 h-1/2 w-[5px] bg-foreground md:w-1.5 dark:bg-ring" />
              <div className="-right-1 pointer-events-none absolute bottom-1 h-1/2 w-[5px] bg-foreground md:w-1.5 dark:bg-ring" />
            </>
          )}
        </>
      )}
    </ShadcnButton>
  );
}

export { Button };

import * as AvatarPrimitive from "@radix-ui/react-avatar";
import { cva } from "class-variance-authority";
import type React from "react";
import { forwardRef } from "react";

import { cn } from "@/lib/utils";

import "./styles/retro.css";

export const avatarVariants = cva("", {
  variants: {
    font: {
      normal: "",
      retro: "retro",
    },
    variant: {
      default: "",
      retro: "",
      pixel: "",
    },
  },
  defaultVariants: {
    font: "retro",
    variant: "pixel",
  },
});

const Avatar = forwardRef<
  React.ComponentRef<typeof AvatarPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof AvatarPrimitive.Root> & {
    font?: "normal" | "retro";
    variant?: "default" | "retro" | "pixel";
  }
>(({ className = "", font, variant = "pixel", ...props }, ref) => {
  const isPixel = variant === "pixel";

  return (
    <div className={cn("relative size-max", className)}>
      {/* Pixel frame (only show if pixel variant) */}
      {isPixel && (
        <div
          className="pointer-events-none absolute inset-0 h-full w-full"
          style={{ zIndex: 10 }}
        >
          {/* Top section - Row 1 */}
          <div className="absolute top-0 right-[23%] left-[23%] h-[7%] bg-foreground dark:bg-ring" />

          {/* Top section - Row 2 */}
          <div className="absolute top-[6.25%] right-[17%] left-[17%] h-[7%] bg-foreground dark:bg-ring" />

          {/* Top section - Row 3 */}
          <div className="absolute top-[12.5%] left-[11%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[12.5%] right-[11%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />

          {/* Top section - Row 4 */}
          <div className="absolute top-[18.75%] left-[5%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[18.75%] right-[5%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />

          {/* Top section - Row 5 */}
          <div className="absolute top-[25%] left-0 h-[7%] w-[20%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[25%] right-0 h-[7%] w-[20%] bg-foreground dark:bg-ring" />

          {/* Top section - Rows 6-7 */}
          <div className="absolute top-[31.25%] left-0 h-[13%] w-[13.5%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[31.25%] right-0 h-[13%] w-[13.5%] bg-foreground dark:bg-ring" />

          {/* Top section - Rows 8-10 */}
          <div className="absolute top-[43.75%] left-0 h-[7%] w-[13.5%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[43.75%] right-0 h-[7%] w-[13.5%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Rows 8-10 (mirror) */}
          <div className="absolute top-[50%] left-0 h-[7%] w-[13.5%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[50%] right-0 h-[7%] w-[13.5%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Rows 6-7 (mirror) */}
          <div className="absolute top-[56.25%] left-0 h-[13%] w-[13.5%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[56.25%] right-0 h-[13%] w-[13.5%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Row 5 (mirror) */}
          <div className="absolute top-[68.75%] left-0 h-[7%] w-[20%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[68.75%] right-0 h-[7%] w-[20%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Row 4 (mirror) */}
          <div className="absolute top-[75%] left-[5%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[75%] right-[5%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Row 3 (mirror) */}
          <div className="absolute top-[81.25%] left-[11%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />
          <div className="absolute top-[81.25%] right-[11%] h-[7%] w-[20%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Row 2 (mirror) */}
          <div className="absolute top-[87.5%] right-[17%] left-[17%] h-[7%] bg-foreground dark:bg-ring" />

          {/* Bottom section - Row 1 (mirror) */}
          <div className="absolute right-[23%] bottom-0 left-[23%] h-[7%] bg-foreground dark:bg-ring" />
        </div>
      )}

      <AvatarPrimitive.Root
        className={cn(
          "relative flex size-10 shrink-0 overflow-hidden text-xs",
          !isPixel && "rounded-none",
          isPixel && "rounded-full",
          font !== "normal" && "retro",
          variant === "retro" && "image-rendering-pixelated",
          className
        )}
        data-slot="avatar"
        ref={ref}
        {...props}
      />

      {/* Original border styling (only show if not pixel variant) */}
      {!isPixel && (
        <>
          <div className="pointer-events-none absolute top-0 left-0 h-1.5 w-full bg-foreground dark:bg-ring" />
          <div className="pointer-events-none absolute bottom-0 h-1.5 w-full bg-foreground dark:bg-ring" />
          <div className="-left-1.5 pointer-events-none absolute top-1.5 h-1/2 w-1.5 bg-foreground dark:bg-ring" />
          <div className="-left-1.5 pointer-events-none absolute bottom-1.5 h-1/2 w-1.5 bg-foreground dark:bg-ring" />
          <div className="-right-1.5 pointer-events-none absolute top-1.5 h-1/2 w-1.5 bg-foreground dark:bg-ring" />
          <div className="-right-1.5 pointer-events-none absolute bottom-1.5 h-1/2 w-1.5 bg-foreground dark:bg-ring" />
        </>
      )}
    </div>
  );
});
Avatar.displayName = AvatarPrimitive.Root.displayName;

interface BitAvatarImageProps
  extends React.ComponentPropsWithoutRef<typeof AvatarPrimitive.Image> {
  font?: "normal" | "retro";
  variant?: "default" | "retro" | "pixel";
}

const AvatarImage = forwardRef<
  React.ComponentRef<typeof AvatarPrimitive.Image>,
  BitAvatarImageProps
>(({ className, font, ...props }, ref) => (
  <AvatarPrimitive.Image
    className={cn(
      "aspect-square h-full w-full",
      font === "retro" && "retro",
      className
    )}
    data-slot="avatar-image"
    ref={ref}
    {...props}
  />
));
AvatarImage.displayName = AvatarPrimitive.Image.displayName;

const AvatarFallback = forwardRef<
  React.ComponentRef<typeof AvatarPrimitive.Fallback>,
  React.ComponentPropsWithoutRef<typeof AvatarPrimitive.Fallback>
>(({ className, ...props }, ref) => (
  <AvatarPrimitive.Fallback
    className={cn(
      "flex h-full w-full items-center justify-center rounded-full bg-muted text-foreground",
      className
    )}
    data-slot="avatar-fallback"
    ref={ref}
    {...props}
  />
));
AvatarFallback.displayName = AvatarPrimitive.Fallback.displayName;

export { Avatar, AvatarImage, AvatarFallback };

import { cva, type VariantProps } from "class-variance-authority";
import { Badge as ShadcnBadge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";

export const badgeVariants = cva("", {
  variants: {
    font: {
      normal: "",
      retro: "retro",
    },
    variant: {
      default: "border-primary bg-primary",
      destructive: "border-destructive bg-destructive",
      outline: "border-background bg-background",
      secondary: "border-secondary bg-secondary",
    },
  },
  defaultVariants: {
    variant: "default",
  },
});

export interface BitButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof badgeVariants> {
  asChild?: boolean;
}

function Badge({
  children,
  className = "",
  font,
  variant,
  ...props
}: BitButtonProps) {
  const color = badgeVariants({ variant, font });

  const classes = className.split(" ");

  // visual classes for badge and sidebars
  const visualClasses = classes.filter(
    (c) =>
      c.startsWith("bg-") ||
      c.startsWith("border-") ||
      c.startsWith("text-") ||
      c.startsWith("rounded-")
  );

  // Container should accept all non-visual utility classes (e.g., size, spacing, layout)
  const containerClasses = classes.filter(
    (c) =>
      !(
        c.startsWith("bg-") ||
        c.startsWith("border-") ||
        c.startsWith("text-") ||
        c.startsWith("rounded-")
      )
  );

  return (
    <div className={cn("relative inline-flex items-stretch", containerClasses)}>
      <ShadcnBadge
        {...props}
        className={cn(
          "h-full",
          "rounded-none",
          "w-full",
          font !== "normal" && "retro",
          visualClasses
        )}
        variant={variant}
      >
        {children}
      </ShadcnBadge>

      {/* Left pixel bar */}
      <div
        className={cn(
          "-left-1.5 absolute inset-y-[4px] w-1.5",
          color,
          visualClasses
        )}
      />
      {/* Right pixel bar */}
      <div
        className={cn(
          "-right-1.5 absolute inset-y-[4px] w-1.5",
          color,
          visualClasses
        )}
      />
    </div>
  );
}

export { Badge };

@import url("https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap");

.retro {
  font-family: "Press Start 2P", system-ui, -apple-system, sans-serif;
  line-height: 1.5;
  letter-spacing: 0.5px;
}
