"use client";
import * as React from "react";

import { type VariantProps, cva } from "class-variance-authority";

import { cn } from "@/lib/utils";

import "./styles/retro.css";

export interface StatusEffect {
  id: string;
  type: "poison" | "freeze" | "burn" | "stun" | "heal" | "shield" | "speed" | "slow";
  duration?: number;
  stacks?: number;
  intensity?: "weak" | "normal" | "strong";
}

export const statusEffectVariants = cva("", {
  variants: {
    variant: {
      default: "",
      retro: "retro",
    },
    size: {
      sm: "w-8 h-8 text-sm",
      md: "w-10 h-10 text-base", 
      lg: "w-12 h-12 text-lg",
      xl: "w-14 h-14 text-xl",
    },
  },
  defaultVariants: {
    variant: "retro",
    size: "md",
  },
});

export interface StatusEffectIndicatorProps
  extends React.ComponentProps<"div">,
    VariantProps<typeof statusEffectVariants> {
  effects: StatusEffect[];
  showDuration?: boolean;
  showStacks?: boolean;
  maxEffects?: number;
  className?: string;
  animated?: boolean;
}

// Pixel art icons for different status effects
