// Get status effect colors
const getStatusEffectColor = (type: StatusEffect["type"], intensity: StatusEffect["intensity"] = "normal") => {
  const colors = {
    poison: {
      weak: "bg-green-500/20 border-green-400 text-green-300",
      normal: "bg-green-600/30 border-green-500 text-green-200",
      strong: "bg-green-700/40 border-green-600 text-green-100",
    },
    freeze: {
      weak: "bg-blue-500/20 border-blue-400 text-blue-300",
      normal: "bg-blue-600/30 border-blue-500 text-blue-200",
      strong: "bg-blue-700/40 border-blue-600 text-blue-100",
    },
    burn: {
      weak: "bg-red-500/20 border-red-400 text-red-300",
      normal: "bg-red-600/30 border-red-500 text-red-200",
      strong: "bg-red-700/40 border-red-600 text-red-100",
    },
    stun: {
      weak: "bg-yellow-500/20 border-yellow-400 text-yellow-300",
      normal: "bg-yellow-600/30 border-yellow-500 text-yellow-200",
      strong: "bg-yellow-700/40 border-yellow-600 text-yellow-100",
    },
    heal: {
      weak: "bg-emerald-500/20 border-emerald-400 text-emerald-300",
      normal: "bg-emerald-600/30 border-emerald-500 text-emerald-200",
      strong: "bg-emerald-700/40 border-emerald-600 text-emerald-100",
    },
    shield: {
      weak: "bg-slate-500/20 border-slate-400 text-slate-300",
      normal: "bg-slate-600/30 border-slate-500 text-slate-200",
      strong: "bg-slate-700/40 border-slate-600 text-slate-100",
    },
    speed: {
      weak: "bg-cyan-500/20 border-cyan-400 text-cyan-300",
      normal: "bg-cyan-600/30 border-cyan-500 text-cyan-200",
      strong: "bg-cyan-700/40 border-cyan-600 text-cyan-100",
    },
    slow: {
      weak: "bg-orange-500/20 border-orange-400 text-orange-300",
      normal: "bg-orange-600/30 border-orange-500 text-orange-200",
      strong: "bg-orange-700/40 border-orange-600 text-orange-100",
    },
  };

  return colors[type][intensity];
};

function StatusEffectBadge({
  effect,
  size,
  showDuration,
  showStacks,
  animated,
}: {
  effect: StatusEffect;
  size: VariantProps<typeof statusEffectVariants>["size"];
  showDuration?: boolean;
  showStacks?: boolean;
  animated?: boolean;
}) {
  const icon = getStatusEffectIcon(effect.type, effect.intensity);
  const colorClass = getStatusEffectColor(effect.type, effect.intensity);

  return (
    <div className="relative">
      {/* Main effect icon */}
      <div
        className={cn(
          "relative flex items-center justify-center border-2 rounded-sm retro transition-all duration-200",
          statusEffectVariants({ size }),
          colorClass,
          animated && "hover:scale-105"
        )}
        title={`${effect.type.charAt(0).toUpperCase() + effect.type.slice(1)}${
          effect.duration ? ` (${effect.duration}s)` : ""
        }${effect.stacks ? ` x${effect.stacks}` : ""}`}
      >
        <span className="text-center leading-none select-none">{icon}</span>
      </div>

      {/* Duration indicator */}
      {showDuration && effect.duration && (
        <div className="absolute -top-1.5 -right-1.5 min-w-5 h-5 bg-black border border-white rounded-sm flex items-center justify-center">
          <span className="text-[10px] text-white font-bold leading-none">
            {effect.duration}
          </span>
        </div>
      )}

      {/* Stack indicator */}
      {showStacks && effect.stacks && effect.stacks > 1 && (
        <div className="absolute -bottom-1.5 -right-1.5 min-w-5 h-5 bg-orange-600 border border-white rounded-sm flex items-center justify-center">
          <span className="text-[10px] text-white font-bold leading-none">
            {effect.stacks}
          </span>
        </div>
      )}
    </div>
  );
}

export function StatusEffectIndicator({
  effects,
  showDuration = true,
  showStacks = true,
  maxEffects = 10,
  className,
  variant,
  size = "md",
  animated = true,
  ...props
}: StatusEffectIndicatorProps) {
  const visibleEffects = React.useMemo(() => {
    return effects.slice(0, maxEffects);
  }, [effects, maxEffects]);

  const remainingCount = effects.length - maxEffects;

  if (effects.length === 0) {
    return null;
  }

  return (
    <div
      className={cn(
        "flex flex-row items-center gap-2 flex-wrap",
        variant === "retro" && "retro",
        className
      )}
      style={{ display: "flex", flexDirection: "row" }}
      {...props}
    >
      {visibleEffects.map((effect) => (
        <StatusEffectBadge
          key={effect.id}
          effect={effect}
          size={size}
          showDuration={showDuration}
          showStacks={showStacks}
          animated={animated}
        />
      ))}

      {/* Show remaining count if there are more effects */}
      {remainingCount > 0 && (
        <div
          className={cn(
            "flex items-center justify-center border-2 border-muted-foreground bg-muted/30 text-muted-foreground rounded-sm retro",
            statusEffectVariants({ size })
          )}
          title={`${remainingCount} more effect${remainingCount > 1 ? "s" : ""}`}
        >
          <span className="text-center leading-none font-bold select-none">+{remainingCount}</span>
        </div>
      )}
    </div>
  );
}

export default StatusEffectIndicator;

@import url("https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap");

.retro {
  font-family:
    "Press Start 2P",
    system-ui,
    -apple-system,
    sans-serif;
  line-height: 1.5;
  letter-spacing: 0.5px;
}
